; ---------------------------------------------------------------
; void __near__ load_room (void)
; ---------------------------------------------------------------

.segment	"CODE"

.proc	_load_room: near

.segment	"CODE"

;
; clear_vram_buffer();
;
	jsr     _clear_vram_buffer
;
; offset = Level_offsets[level];
;
	ldy     _level
	lda     _Level_offsets,y
	sta     _offset
;
; set_data_pointer(Levels_list[offset]);
;
	ldx     #$00
	lda     _offset
	asl     a
	bcc     L0019
	inx
	clc
L0019:	adc     #<(_Levels_list)
	sta     ptr1
	txa
	adc     #>(_Levels_list)
	sta     ptr1+1
	ldy     #$01
	lda     (ptr1),y
	tax
	dey
	lda     (ptr1),y
	jsr     _set_data_pointer
;
; set_mt_pointer(metatiles);
;
	lda     #<(_metatiles)
	ldx     #>(_metatiles)
	jsr     _set_mt_pointer
;
; for(y=0; ;y+=0x20){
;
	lda     #$00
L0017:	sta     _y
;
; for(x=0; ;x+=0x20){
;
	lda     #$00
L0016:	sta     _x
;
; address = get_ppu_addr(0, x, y);
;
	jsr     decsp2
	lda     #$00
	ldy     #$01
	sta     (sp),y
	lda     _x
	dey
	sta     (sp),y
	lda     _y
	jsr     _get_ppu_addr
	sta     _address
	stx     _address+1
;
; index = (y & 0xf0) + (x >> 4);
;
	lda     _y
	and     #$F0
	sta     ptr1
	lda     _x
